import { ScrollView, View } from "react-native";
import React from "react";
import AppBar from "../../components/Reusable/Appbar";
import { COLORS, SIZES } from "../../constants/theme";
import styles from "./hotelDetails.style";
import { HeightSpacer, NetworkImage, ReusableText } from "../../components";

const HotelDetails = ({ navigation }) => {
  const hotel = {
    availability: {
      start: "2023-08-20T00:00:00.000Z",
      end: "2023-08-25T00:00:00.000Z",
    },
    _id: "64c675793cfa5e847bcd5436",
    country_id: "64c62bfc65af9f8c969a8d04",
    title: "Urban Chic Boutique Hotel",
    description:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Mauris sit amet massa vitae tortor condimentum lacinia quis. Elit ut aliquam purus sit amet luctus. Nunc mi ipsum faucibus vitae aliquet. Et magnis dis parturient montes nascetur ridiculus mus mauris. Vel fringilla est ullamcorper eget nulla facilisi.",
    contact: "64c5d95adc7efae2a45ec376",
    imageUrl:
      "https://images.unsplash.com/photo-1445991842772-097fea258e7b?w=600&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8NHx8VXJiYW4lMjBDaGljJTIwQm91dGlxdWUlMjBIb3RlbHxlbnwwfHwwfHx8MA%3D%3D",
    rating: 4.8,
    review: "2312 Reviews",
    location: "San Francisco, CA",
    latitude: 37.7749,
    longitude: -122.4194,
    price: 400,
    facilities: [
      {
        wifi: true,
        _id: "64c675793cfa5e847bcd5437",
      },
    ],
    __v: 0,
  };

  return (
    <ScrollView>
      <View style={{ height: 80 }}>
        <AppBar
          title="Hotel Details"
          color={COLORS.white}
          icon="search1"
          color1={COLORS.white}
          onPress={() => navigation.goBack()}
          onPress1={() => {}}
          top={50}
          left={20}
          right={20}
        />
      </View>

      <View style={styles.container}>
        <NetworkImage
          source={hotel.imageUrl}
          width="100%"
          height={220}
          radius={25}
        />

        <View style={styles.titleContainer}>
          <View style={styles.titleColumn}>
            <ReusableText
              text={hotel.title}
              family="medium"
              size={SIZES.xLarge}
              color={COLORS.black}
            />

            <HeightSpacer height={10} />

            <ReusableText
              text={hotel.location}
              family="medium"
              size={SIZES.medium}
              color={COLORS.black}
            />
            <HeightSpacer height={10} />

          </View>
        </View>
      </View>
    </ScrollView>
  );
};

export default HotelDetails;
