import { useFonts } from "expo-font";
import { useCallback } from "react";
import * as SplashScreen from "expo-splash-screen";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import {
  BottomTabNavigation,
  CountryDetails,
  HotelDetails,
  HotelList,
  HotelSearch,
  Onboarding,
  PlaceDetails,
  Recommended,
  Search,
  SelectRoom,
} from "./screens";

const Stack = createNativeStackNavigator();

export default function App() {
  const [fontLoader] = useFonts({
    light: require("./assets/fonts/light.otf"),
    regular: require("./assets/fonts/regular.otf"),
    medium: require("./assets/fonts/medium.otf"),
    bold: require("./assets/fonts/bold.otf"),
    xtrabold: require("./assets/fonts/xtrabold.otf"),
  });

  const onLayoutRootView = useCallback(async () => {
    if (fontLoader) {
      await SplashScreen.hideAsync();
    }
  }, [fontLoader]);

  if (!fontLoader) {
    return null;
  }

  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen
          name="Onboard"
          component={Onboarding}
          options={{
            headerShown: false,
          }}
        />
        <Stack.Screen
          name="Bottom"
          component={BottomTabNavigation}
          options={{
            headerShown: false,
          }}
        />
        <Stack.Screen
          name="Search"
          component={Search}
          options={{
            headerShown: false,
          }}
        />
        <Stack.Screen
          name="CountryDetails"
          component={CountryDetails}
          options={{
            headerShown: false,
          }}
        />
        <Stack.Screen
          name="Recommended"
          component={Recommended}
          options={{
            headerShown: false,
          }}
        />
        <Stack.Screen
          name="PlaceDetails"
          component={PlaceDetails}
          options={{
            headerShown: false,
          }}
        />
        <Stack.Screen
          name="HotelDetails"
          component={HotelDetails}
          options={{
            headerShown: false,
          }}
        />
        <Stack.Screen
          name="HotelList"
          component={HotelList}
          options={{
            headerShown: false,
          }}
        />
        <Stack.Screen
          name="HotelSearch"
          component={HotelSearch}
          options={{
            headerShown: false,
          }}
        />
        <Stack.Screen
          name="SelectRoom"
          component={SelectRoom}
          options={{
            headerShown: false,
          }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}
